import gulp from 'gulp';
import babel from 'gulp-babel';
import watch from 'gulp-watch';
import log from 'fancy-log';
import fs from 'fs';
import async from 'async';
import path from 'path';
import mjml2html from 'mjml';
import { registerComponent } from 'mjml-core';

const walkSync = (dir, filelist = []) => {
  fs.readdirSync(dir).forEach((file) => {
    filelist = fs.statSync(path.join(dir, file)).isDirectory()
      ? walkSync(path.join(dir, file), filelist)
      : filelist.concat(path.join(dir, file));
  });
  return filelist;
};

const watchedComponents = walkSync('./components');

const compile = () => {
  if (!fs.existsSync('./dist')) {
    fs.mkdirSync('./dist');
  }

  return gulp
    .src(path.normalize('components/**/*.js'))
    .pipe(
      babel({
        presets: ['@babel/preset-env'],
      })
    )
    .on('error', log)
    .pipe(gulp.dest('lib'))
    .on('end', () => {
      watchedComponents.forEach((compPath) => {
        const fullPath = path.join(
          process.cwd(),
          compPath.replace(/^components/, 'lib')
        );
        delete require.cache[fullPath];
        registerComponent(require(fullPath).default);
      });

      fs.readdir('snippets/', (error, files) => {
        if (error) throw error;
        const mjmlFilesList = files
          .filter((e) => path.extname(e).toLowerCase() === '.mjml')
          .map((file) => `snippets/${file}`);

        async.eachSeries(mjmlFilesList, (filename, cb) => {
          fs.readFile(filename, 'utf8', (err, data) => {
            if (err) throw err;
            const result = mjml2html(data);
            fs.writeFileSync(
              `dist/${filename
                .replace(/\.mjml$/, '.html')
                .replace(/^snippets\//, '')}`,
              result.html
            );
          });
          cb();
        });
      });
    });
};

gulp.task('build', compile);

gulp.task('watch', () => {
  compile();
  return watch(
    [path.normalize('components/**/*.js'), path.normalize('index.mjml')],
    compile
  );
});

